package uz.gita.contactworkteam.diimport android.content.Contextimport androidx.room.Roomimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.android.qualifiers.ApplicationContextimport dagger.hilt.components.SingletonComponentimport uz.gita.contactworkteam.data.source.local.database.AppDatabaseimport uz.gita.contactworkteam.data.source.local.room.dao.ContactDaoimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)class DatabaseModule {    @[Provides Singleton]    fun providesDatabase(@ApplicationContext context: Context): AppDatabase =        Room.databaseBuilder(context, AppDatabase::class.java, "contacts.db").build()    @[Provides Singleton]    fun providesDao(database: AppDatabase): ContactDao = database.contactDao()}