package uz.gita.contactworkteam.diimport android.content.Contextimport com.chuckerteam.chucker.api.ChuckerInterceptorimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.android.qualifiers.ApplicationContextimport dagger.hilt.components.SingletonComponentimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryimport retrofit2.createimport uz.gita.contactworkteam.data.source.remote.api.ContactApiimport uz.gita.contactworkteam.utils.Constants.BASE_URLimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)class NetworkModule {    @[Provides Singleton]    fun providesClient(@ApplicationContext context: Context): OkHttpClient =        OkHttpClient.Builder().addInterceptor(ChuckerInterceptor.Builder(context).build()).build()    @[Provides Singleton]    fun providesRetrofit(client: OkHttpClient): Retrofit =        Retrofit.Builder()            .baseUrl(BASE_URL) //Before, check BASE_URL, it might be not turned on!            .client(client)            .addConverterFactory(GsonConverterFactory.create())            .build()    @[Provides]    fun provideApi(retrofit: Retrofit): ContactApi = retrofit.create()}