package uz.gita.contactworkteam.presenter.screens.mainimport uz.gita.contactworkteam.domain.models.ContactParamimport uz.gita.contactworkteam.navigator.AppNavigatorimport uz.gita.contactworkteam.presenter.screens.add.AddScreenimport uz.gita.contactworkteam.presenter.screens.edit.EditScreenimport javax.inject.Injectclass MainDirection @Inject constructor(    private val appNavigator: AppNavigator,) : MainContract.MainDirection {    override suspend fun openAddScreen() {        appNavigator.openWithSave(AddScreen())    }    override suspend fun openEditScreen(contactParam: ContactParam) {        appNavigator.openWithSave(EditScreen(contactParam))    }}